@app.route(
    "/api/list_keys", 
    methods = ["POST"]
)
def list_keys():
    d = dict(
        success="true",
        keys=["key{}".format(i) for i in range(random.randint(0, 100))]
    )
    return d

@app.route(
    "/api/key/<key>", 
    methods = ["POST"]
)
def retrieve_image(key):
    content = {
        "key1": "data:image/gif;base64,",
        "key2": "data:image/png;base64,"
    }
    d = dict(
        success="true",
        content=content[key]
    )
    return d

@app.route(
    "/api/config",
    methods=["GET", "POST"]
)
def config():
    global config_base
    if request.method == "POST":
        config_base = json.loads(request.get_data())
    d = dict(
        success="true",
        config=config_base
    )
    return d

