import random, pathlib, base64
from server.config import Config
from server.libs.database import Database
from server.routes.config import memcache_url
import json
import requests

DB = Database()
CONFIG = Config().fetch()

def create_key(args):
    """
        1. Check if key name is valid
        2. Store file
        3. invalidate cache
        4. add/update entry to database
    """
    if args["key"].includes(" "):
        return False, 400, dict(
            message="Key cannot contain spaces"
        )
    file = args["file"]
    if not file.content_type.startswith("image/"):
        return False, 400, "Only image file is allowed."
    file_fullpath = DB.get_key_image_pair(args["key"])
    if not file_fullpath:
        filepath = pathlib.Path.cwd().joinpath(CONFIG["server"]["upload_location"])
        filepath.mkdir(parents=True, exist_ok=True)
        file_ext = file.filename.split(".")[-1]
        filename = "{}.{}".format(args["key"], file_ext) # TODO: use time as filename
        file_fullpath = filepath.joinpath(filename)
        DB.create_key_image_pair(args["key"], file_fullpath)
    else:
        # invalidate the key in the memcache
        response = requests.delete(memcache_url + "/api/cache/key", data=[("key", args["key"])])
    file.save(file_fullpath)
    return True, 200, None

def get_key(key):
    """
        1. ask cache if key-image exists
        2. If key exists, get the image from cache response
        3. If cache does not exist, get the key-image pair from database
        4. If key-image pair exists, get the image from local storage, convert to base64 and response to both webpage and cache
        5. If key-image pair does not exist, return 404 
    """
    # TODO: 1 - 2 completeed, needs to be tested
    response = json.loads(requests.post(memcache_url + "/api/cache/key", data=[("key", key)]))
    if response['success'] == 'true': # if key exists in memcache, get it from the http responce
        return True, 200, dict(
            content=response['content']
        )
    else: 
        key_image_pair = DB.get_key_image_pair(key)
        if not key_image_pair:
            return False, 404, "No such key."
        content = {
            "key1": "data:image/gif;base64,",
            "key2": "data:image/png;base64,"
        }
        with open(pathlib.Path.cwd().joinpath(CONFIG["server"]["upload_location"], "utoronto.svg"), "rb") as f:
            image_content = f.read()
            encoded_bytes = base64.b64encode(image_content)
            humanreadable_data = encoded_bytes.decode("utf-8")
            content["utoronto"] = "data:{};base64,".format("image/svg+xml") + humanreadable_data
            
        # the key does not exist in memcache, so this is a cache miss, put it into the memcache
        response = json.loads(requests.post(memcache_url + "/api/cache/content", data=[('key', key), ('value', content[key])]))
        if response['success'] == 'false': # handle cases like the value is larger then the whole cache size
            pass # TODO: maybe need to do something?
        return True, 200, dict(
            content=content[key]
        )

